binding.script:
var spd_mode = getprop("/flight-management/control/spd-mode");

if (spd_mode == "ias") {

	var ias = getprop("/flight-management/fcu-values/ias");

	if (350 > ias)
		setprop("/flight-management/fcu-values/ias", ias + 1);

} else {

	var mach = getprop("/flight-management/fcu-values/mach");

	if (0.89 > mach)
		setprop("/flight-management/fcu-values/mach", mach + 0.01);

}

var spd_mode = getprop("/flight-management/control/spd-mode");

if (spd_mode == "ias") {

	var ias = getprop("/flight-management/fcu-values/ias");

	if (ias > 0)
		setprop("/flight-management/fcu-values/ias", ias - 1);

} else {

	var mach = getprop("/flight-management/fcu-values/mach");

	if (mach > 0)
		setprop("/flight-management/fcu-values/mach", mach - 0.01);

}


var hdg_prop = "/flight-management/fcu-values/hdg";

if (359 > getprop(hdg_prop))
	setprop(hdg_prop, getprop(hdg_prop) + 1);
else
	setprop(hdg_prop, 0);


var hdg_prop = "/flight-management/fcu-values/hdg";

if (getprop(hdg_prop) > 0)
	setprop(hdg_prop, getprop(hdg_prop) - 1);
else
	setprop(hdg_prop, 359);


var step = getprop("/flight-management/control/alt-sel-mode");

var alt_prop = "/flight-management/fcu-values/alt";

if (43000 > getprop(alt_prop))
	setprop(alt_prop, getprop(alt_prop) + step);


var step = getprop("/flight-management/control/alt-sel-mode");

var alt_prop = "/flight-management/fcu-values/alt";

if (getprop(alt_prop) > 0)
	setprop(alt_prop, getprop(alt_prop) - step);


var ver_ctrl = getprop("/flight-management/control/ver-ctrl");
var ver_mode = getprop("/flight-management/control/ver-mode");

if(ver_ctrl == "man-set") {
	if(ver_mode == "alt-hold") {
		setprop("/flight-management/control/ver-mode", "alt");
	} else {
		setprop("/flight-management/control/ver-ctrl", "fmgc");
	}
} else {
	setprop("/flight-management/control/ver-ctrl", "man-set");
	setprop("/flight-management/control/ver-mode", "alt");
}


var sub_mode = getprop("/flight-management/control/ver-sub");

var vs_prop = "/flight-management/fcu-values/vs";

var fpa_prop = "/flight-management/fcu-values/fpa";

if (sub_mode == "vs") {

	if (3000 > getprop(vs_prop))
		setprop(vs_prop, getprop(vs_prop) + 100);

} else {

	if (15 > getprop(fpa_prop))
		setprop(fpa_prop, getprop(fpa_prop) + 1);

}


var sub_mode = getprop("/flight-management/control/ver-sub");

var vs_prop = "/flight-management/fcu-values/vs";

var fpa_prop = "/flight-management/fcu-values/fpa";

if (sub_mode == "vs") {

	if (getprop(vs_prop) > -2500)
		setprop(vs_prop, getprop(vs_prop) - 100);

} else {

	if (getprop(fpa_prop) > -10)
		setprop(fpa_prop, getprop(fpa_prop) - 1);

}
