binding.script:
	var engine = 0;

	var prot_open = getprop("/controls/hydraulics/engine["~engine~"]/prot-ab");
	var value = getprop("/controls/hydraulics/engine["~engine~"]/pump-ab");
	
	if(prot_open) {
		if(value) {
			setprop("/controls/hydraulics/engine["~engine~"]/pump-ab", 0);
			setprop("/controls/hydraulics/engine["~engine~"]/pump-a", 0);
			setprop("/controls/hydraulics/engine["~engine~"]/pump-b", 0);
		} else {
			setprop("/controls/hydraulics/engine["~engine~"]/pump-ab", 1);
			setprop("/controls/hydraulics/engine["~engine~"]/pump-a", 1);
			setprop("/controls/hydraulics/engine["~engine~"]/pump-b", 1);
		}
	} else {
		print("Open switch protector first!");
	}

	var engine = 1;

	var prot_open = getprop("/controls/hydraulics/engine["~engine~"]/prot-ab");
	var value = getprop("/controls/hydraulics/engine["~engine~"]/pump-ab");
	
