		} else {
			setprop("/controls/hydraulics/engine["~engine~"]/pump-ab", 1);
			setprop("/controls/hydraulics/engine["~engine~"]/pump-a", 1);
			setprop("/controls/hydraulics/engine["~engine~"]/pump-b", 1);
		}
	} else {
		print("Open switch protector first!");
	}

	var engine = 2;

	var prot_open = getprop("/controls/hydraulics/engine["~engine~"]/prot-ab");
	var value = getprop("/controls/hydraulics/engine["~engine~"]/pump-ab");
	
	if(prot_open) {
		if(value) {
			setprop("/controls/hydraulics/engine["~engine~"]/pump-ab", 0);
			setprop("/controls/hydraulics/engine["~engine~"]/pump-a", 0);
			setprop("/controls/hydraulics/engine["~engine~"]/pump-b", 0);
		} else {
			setprop("/controls/hydraulics/engine["~engine~"]/pump-ab", 1);
			setprop("/controls/hydraulics/engine["~engine~"]/pump-a", 1);
			setprop("/controls/hydraulics/engine["~engine~"]/pump-b", 1);
		}
	} else {
		print("Open switch protector first!");
	}

	var engine = 3;

	var prot_open = getprop("/controls/hydraulics/engine["~engine~"]/prot-ab");
	var value = getprop("/controls/hydraulics/engine["~engine~"]/pump-ab");
	
	if(prot_open) {
		if(value) {
			setprop("/controls/hydraulics/engine["~engine~"]/pump-ab", 0);
			setprop("/controls/hydraulics/engine["~engine~"]/pump-a", 0);
			setprop("/controls/hydraulics/engine["~engine~"]/pump-b", 0);
		} else {
			setprop("/controls/hydraulics/engine["~engine~"]/pump-ab", 1);
			setprop("/controls/hydraulics/engine["~engine~"]/pump-a", 1);
			setprop("/controls/hydraulics/engine["~engine~"]/pump-b", 1);
		}
	} else {
		print("Open switch protector first!");
	}

var prot_open = getprop("/controls/fuel/jettison/arm-prot");
var value = getprop("/controls/fuel/jettison/arm");

if(prot_open) {
	if(value) {
		setprop("/controls/fuel/jettison/arm", 0);
	} else {
		setprop("/controls/fuel/jettison/arm", 1);
	}
} else {
	print("Open switch cage first!");
}

var prot_open = getprop("/controls/fuel/jettison/active-prot");
var value = getprop("/controls/fuel/jettison/active");

if(prot_open) {
	if(value) {
		setprop("/controls/fuel/jettison/active", 0);
	} else {
		setprop("/controls/fuel/jettison/active", 1);
	}
} else {
	print("Open switch cage first!");
}
